import React from 'react';
import logo from './logo.svg';
import styles from './App.css';



class BoxController extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      theme: 'dark',
    };
    this.childElement = React.createRef();
  }
  setChildTheme = () => {
    this.childElement.current.setTheme(this.state.theme);
  }
  render() {
    return(
      <div className="box-controller">
        <div className="box-bar">
          <div
            className={
              "bar bar-base theme-"+
              this.state.theme
            }
          >
          </div>

        </div>
        <div className="box-buttons">
          <ButtonSmall ref={this.childElement} name="btnRewind" alignRight={ false } onClick={this.setChildTheme} />
          <ButtonSmall name="btnSkip" alignRight={ false } onload={this.setChildTheme} />
          <ButtonSmall name="btnExit" alignRight={ true } onload={this.setChildTheme} />
        </div>
      </div>
    );
  }
}

class ButtonSmall extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      theme: 'light',
      clicked: false
    };
  }
  handleClick = () => {
    this.setState({ clicked: true })
  }
  setTheme = (targetTheme) => {
    this.setState({ theme: targetTheme })
  }
  render() {
    return (
      <button
        id={this.props.name}
        className={
          "button-small theme-"+
          this.state.theme+
          (this.props.alignRight ? ' align-right' : '')
        }
      >
      </button>
    );
  }
}


function App() {
  const element = <BoxController />;
  return(element);
}
export default App;
