class AudioController extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      message: '',
      displayMessage: '',
      displayOnly: 0,
      hasImage: 0,
      epd: 0,
      answerEntity: '',
      maxTimeout: 0,
      duration: 0,
      currentPlayTime: 0,
      playStatus: 0,
      theme: 'light',
    };
  }
  getData = () => {

  }/*
  render() {
    return(
      true;
    );
  }
  */
}

class Baseplate extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      playStatus: 0,
      theme: 'light',
      message: ''
    };
  }
  render() {
    return(
      <div id="baseplate">
        <BoxAgent />
        <BoxController />
      </div>
    );
  }
}

/* * * * * * * * * * * * *
 * BoxAgent and children *
 * * * * * * * * * * * * */

class BoxAgent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      playStatus: 0,
      message: "dummy text"
    };
  }
  render() {
    return(
      <div className="box-agent">
        <ButtonAgent playStatus={this.state.playStatus} />
        <TextSubtitle />
      </div>
    );
  }
}
class ButtonAgent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      playStatus: 0
    };
  }
  setButtonStatus = (param) => {
    switch(param) {
      case 0: //audio is not loaded
        return 'idle';
      case 1: //audio is ready
        return 'idle';
      case 2: //audio is playing
        return 'listening';
      case 3: //audio ended
        return 'idle';
      default:
        return 'idle';
    }
  }
  render() {
    return(
      <button
        id="btnAgent"
        className={"button-agent status-"+this.setButtonStatus(this.props.playStatus)}
      >
      </button>
    );
  }
}
class TextSubtitle extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      theme: 'light',
      message: "dummy text"
    };
  }
  render() {
    return(
      <p
        id="txtSubtitle"
        className={"theme-"+this.state.theme}
      >
        {this.state.message}
      </p>
    );
  }
}


