import React from 'react';
import logo from './logo.svg';
import styles from './App.css';


class BtnSmall extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      theme: 'light',
      clicked: 0
    };
  }
  render() {
    return (
      <button
        id={this.props.name}
        className={
          "button-small theme-"+
          this.state.theme+
          this.props.alignRight ? 'align-right' : ''
        }
      >
      </button>
    );
  }
}

class BoxController extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      theme: 'light',
    };
  }
  render() {
    return(
      <div className="box-controller">
        <div className="box-bar">
          <div
            className={
              "bar bar-base theme-"+
              this.state.theme
            }
          >
          </div>

        </div>
        <div className="box-buttons">
          <BtnSmall name="btnRewind" />
          <BtnSmall name="btnSkip" />
          <BtnSmall name="btnExit" alignRight={true} />
        </div>
      </div>
    );
  }
}

function App() {
  const element = <BoxController />;
  return(element);
}
export default App;
