import React from 'react';
import logo from './logo.svg';
import styles from './App.css';


class ButtonSmall extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      theme: 'light',
      clicked: false
    };
  }
  handleClick = () => this.setState({ clicked: true })
  setTheme = (targetTheme) => this.setState({ theme: targetTheme })
  render() {
    return (
      <button
        id={this.props.name}
        className={
          "button-small theme-"+
          this.state.theme+
          (this.props.alignRight ? ' align-right' : '')
        }
      >
      </button>
    );
  }
}

class BoxController extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      theme: 'light',
    };
  }
  render() {
    return(
      <div className="box-controller">
        <div className="box-bar">
          <div
            className={
              "bar bar-base theme-"+
              this.state.theme
            }
          >
          </div>

        </div>
        <div className="box-buttons">
          <ButtonSmall name="btnRewind" alignRight={ false } />
          <ButtonSmall name="btnSkip" alignRight={ false } />
          <ButtonSmall name="btnExit" alignRight={ true } />
        </div>
      </div>
    );
  }
}

function App() {
  const element = <BoxController />;
  return(element);
}
export default App;
