class ButtonAgent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      playStatus: 0,
    };
  }
  static getDerivedStateFromProps(props, state) {
    return {
      playStatus: props.playStatus
    };
  }
  setButtonStatus = (param) => {
    switch(param) {
      case 0: //audio is not loaded
        return 'idle';
      case 1: //audio is ready
        return 'idle';
      case 2: //audio is playing
        return 'listening';
      case 3: //audio ended
        return 'idle';
      default:
        return 'idle';
    }
  }
  render() {
    return(
      <button
        id="btnAgent"
        className={"button-agent status-"+this.setButtonStatus(this.state.playStatus)}
        onClick={this.props.recHandler}
      >
      </button>
    );
  }
}

